import requests
import base64
from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Data


class GraphGenerator(Component):
    display_name = "Graph Generator"
    description = "Generates graphs based on the input configuration."
    documentation: str = "http://docs.langflow.org/components/custom"
    icon = "Globe"
    name = "GraphGenerator"

    inputs = [
        MessageTextInput(
            name="input_value",
            display_name="Input Value",
            info="This is a custom component Input",
            value="Hello, World!",
        ),
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output"),
    ]

    def build_output(self) -> Message:
        chart_config = """{
          type: "line",
          data: {
            labels: ['2023Q1', '2023Q2', '2023Q3', '2023Q4'],
            datasets: [
              {
                label: "TSMC",
                data: [30929, 32055, 33728, 34833],
                borderColor: "blue",
                fill: "False"
              },
              {
                label: "Foxconn",
                data: [16028, 15843, 16871, 16862],
                borderColor: "red",
                fill: "False"
              }
            ]
          }
        }"""

        if self.input_value == "無":
            image = ""
        else:
            chart_url = "https://quickchart.io/chart?c=" + requests.utils.quote(self.input_value)

            response = requests.get(chart_url)

            if response.status_code == 200:
                img_base64 = base64.b64encode(response.content).decode('utf-8')
                img_markdown = f"![img](data:image/png;base64,{img_base64})"
                image = img_markdown
            else:
                image = ""

        return image

